CREATE PROCEDURE [Audit].[sp_ObtenerAuditoriaUsuarios]
    @Usuario VARCHAR(50) = NULL,
    @Accion VARCHAR(10) = NULL,
    @FechaDesde DATETIME = NULL,
    @FechaHasta DATETIME = NULL
AS
BEGIN
    SELECT *
    FROM audit.Usuarios_Historial
    WHERE (@Usuario IS NULL OR CambiadoPor = @Usuario)
      AND (@Accion IS NULL OR Accion = @Accion)
      AND (@FechaDesde IS NULL OR FechaCambio >= @FechaDesde)
      AND (@FechaHasta IS NULL OR FechaCambio <= @FechaHasta)
    ORDER BY FechaCambio DESC
END

