CREATE PROCEDURE [Audit].[sp_ObtenerHistorialActual]
	@Schema VARCHAR(255),
	@NombreTabla VARCHAR(255),
	@Id_entidad INT
AS
BEGIN
	DECLARE @sql NVARCHAR(MAX)

	-- Armar el SQL dinámico de forma segura
	SET @sql = N'SELECT * FROM ' + QUOTENAME(@Schema) +'.' +QUOTENAME(@NombreTabla) +
	           N' WHERE EsUltimaVersion = 1 AND Id = @Id_entidad'

	-- Ejecutar con parámetro
	EXEC sp_executesql @sql, N'@Id_entidad INT', @Id_entidad = @Id_entidad
END
