CREATE PROCEDURE [Audit].[sp_RestaurarUsuarioDesdeAuditoria]
    @id_usuario INT,
    @version INT
AS
BEGIN
    SET NOCOUNT ON;

    DECLARE @Username NVARCHAR(50),
            @PasswordHash NVARCHAR(100),
            @FechaCreacion DATETIME,
            @idioma_id INT,
            @id_rol INT,
            @DVH NVARCHAR(50),
            @Email NVARCHAR(100),
            @Accion NVARCHAR(10),
            @RolPorDefecto INT = 712;

    -- Obtener datos desde la auditoría
    SELECT 
        @Username = Username,
        @PasswordHash = PasswordHash,
        @FechaCreacion = FechaCreacion,
        @idioma_id = idioma_id,
        @id_rol = id_rol,
        @DVH = DVH,
        @Email = Email,
        @Accion = Accion
    FROM audit.Usuarios_Historial
    WHERE Id = @id_usuario AND Version = @version;

    IF @Accion IS NULL
    BEGIN
        RAISERROR('No se encontró la versión especificada en Usuarios_Historial!', 16, 1);
        RETURN;
    END

    IF @Accion = 'DELETE'
    BEGIN
        -- Si el histórico fue un delete, eliminamos el usuario si existe
        IF EXISTS (SELECT 1 FROM dbo.Usuarios WHERE id_usuario = @id_usuario)
        BEGIN
            DELETE FROM dbo.Usuarios WHERE id_usuario = @id_usuario;
        END
    END
    ELSE
    BEGIN
        IF NOT EXISTS (SELECT 1 FROM dbo.permiso WHERE id = @id_rol)
        BEGIN
            SET @id_rol = @RolPorDefecto;
        END

        IF EXISTS (SELECT 1 FROM dbo.Usuarios WHERE id_usuario = @id_usuario)
        BEGIN
            UPDATE dbo.Usuarios
            SET
                Username = @Username,
                PasswordHash = @PasswordHash,
                FechaCreacion = @FechaCreacion,
                idioma_id = @idioma_id,
                id_rol = @id_rol,
                DVH = @DVH,
                Email = @Email
            WHERE id_usuario = @id_usuario;
        END
        ELSE
        BEGIN
            SET IDENTITY_INSERT dbo.Usuarios ON;

            INSERT INTO dbo.Usuarios
                (id_usuario, Username, PasswordHash, FechaCreacion, idioma_id, id_rol, DVH, Email)
            VALUES
                (@id_usuario, @Username, @PasswordHash, @FechaCreacion, @idioma_id, @id_rol, @DVH, @Email);

            SET IDENTITY_INSERT dbo.Usuarios OFF;
        END
    END
END;

