  CREATE PROCEDURE [Audit].[sp_RestaurarVersionUsuario]
    @Id_Usuario INT,
    @Version INT,
    @CambiadoPor NVARCHAR(100)
AS
BEGIN
    SET NOCOUNT ON;

    DECLARE @FechaCambio DATETIME = GETDATE();

    -- Obtener datos de la versión anterior
    DECLARE @Username NVARCHAR(100),
            @PasswordHash NVARCHAR(255),
            @FechaCreacion DATETIME,
            @idioma_id INT,
            @id_rol INT,
            @DVH NVARCHAR(50),
            @Email NVARCHAR(255);

    SELECT 
        @Username = Username,
        @PasswordHash = PasswordHash,
        @FechaCreacion = FechaCreacion,
        @idioma_id = idioma_id,
        @id_rol = id_rol,
        @DVH = DVH,
        @Email = Email
    FROM audit.Usuarios_Historial
    WHERE Id = @Id_Usuario AND Version = @Version;

    -- Invalidar versiones actuales
    UPDATE audit.Usuarios_Historial
    SET EsUltimaVersion = 0
    WHERE Id = @Id_Usuario;

    -- Insertar como nueva versión
    INSERT INTO audit.Usuarios_Historial (
        Id, Username, PasswordHash, FechaCreacion, idioma_id, id_rol, DVH, Email,
        Version, Accion, CambiadoPor, FechaCambio, EsUltimaVersion
    )
    SELECT 
        @Id_Usuario, @Username, @PasswordHash, @FechaCreacion, @idioma_id, @id_rol, @DVH, @Email,
        (SELECT ISNULL(MAX(Version), 0) + 1 FROM audit.Usuarios_Historial WHERE Id = @Id_Usuario),
        'Restauración', @CambiadoPor, @FechaCambio, 1;
END
