CREATE PROCEDURE [dbo].[sp_AceptarPeticionRestauracion]
    @IdPeticion INT,
    @UsuarioAutorizador NVARCHAR(255)
AS
BEGIN
    SET NOCOUNT ON;

    DECLARE @IdCambio UNIQUEIDENTIFIER,
            @Tabla NVARCHAR(255),
            @Registro INT,
            @ColumnaClave NVARCHAR(255),
            @Esquema NVARCHAR(255) = 'dbo',  -- Valor por defecto
            @NombreTabla NVARCHAR(255);

    -- Obtener los datos de la petición
    SELECT
        @IdCambio = IdCambio,
        @Tabla = Tabla,
        @Registro = Registro
    FROM PeticionRestauracion
    WHERE IdPeticion = @IdPeticion AND Estado = 'Pendiente';

    IF @IdCambio IS NULL
    BEGIN
        THROW 51001, 'Petición no encontrada o ya procesada.', 1;
    END

    -- Separar esquema y nombre de tabla si viene con "dbo.Tabla"
    IF CHARINDEX('.', @Tabla) > 0
    BEGIN
        SET @Esquema = PARSENAME(@Tabla, 2);
        SET @NombreTabla = PARSENAME(@Tabla, 1);
    END
    ELSE
    BEGIN
        SET @NombreTabla = @Tabla;
    END

    -- Obtener la clave primaria
    SELECT TOP 1 @ColumnaClave = KU.COLUMN_NAME
    FROM INFORMATION_SCHEMA.TABLE_CONSTRAINTS AS TC
    INNER JOIN INFORMATION_SCHEMA.KEY_COLUMN_USAGE AS KU
        ON TC.CONSTRAINT_NAME = KU.CONSTRAINT_NAME
        AND TC.TABLE_SCHEMA = KU.TABLE_SCHEMA
    WHERE TC.TABLE_NAME = @NombreTabla
      AND TC.TABLE_SCHEMA = @Esquema
      AND TC.CONSTRAINT_TYPE = 'PRIMARY KEY';

    IF @ColumnaClave IS NULL
    BEGIN
        THROW 51002, 'No se encontró la clave primaria de la tabla especificada.', 1;
    END

    -- Cursor para recorrer los campos a restaurar
    DECLARE cambios_cursor CURSOR FOR
    SELECT Campo, ValorDespues
    FROM AuditoriaTablas
    WHERE id_cambio = @IdCambio;

    DECLARE @Campo NVARCHAR(255),
            @ValorNuevo NVARCHAR(MAX),
            @ValorActual NVARCHAR(MAX);

    OPEN cambios_cursor;
    FETCH NEXT FROM cambios_cursor INTO @Campo, @ValorNuevo;

    WHILE @@FETCH_STATUS = 0
    BEGIN
        -- Obtener el valor actual
        SELECT TOP 1 @ValorActual = ValorDespues
        FROM AuditoriaTablas
        WHERE Tabla = @Tabla AND Registro = @Registro AND Campo = @Campo
        ORDER BY Fecha DESC;

        -- Registrar restauración
        INSERT INTO AuditoriaTablas (
            Tabla, Campo, ValorAntes, ValorDespues, Fecha,
            Tipo, Registro, id_cambio, Usuario
        )
        VALUES (
            @Tabla, @Campo, @ValorActual, @ValorNuevo, GETDATE(),
            'R', @Registro, NEWID(), @UsuarioAutorizador
        );

        -- Ejecutar el UPDATE dinámico
        DECLARE @sql NVARCHAR(MAX) = N'
            UPDATE ' + QUOTENAME(@Esquema) + '.' + QUOTENAME(@NombreTabla) + '
            SET ' + QUOTENAME(@Campo) + ' = @nuevoValor
            WHERE ' + QUOTENAME(@ColumnaClave) + ' = @registroId';

        EXEC sp_executesql @sql,
            N'@nuevoValor NVARCHAR(MAX), @registroId INT',
            @nuevoValor = @ValorNuevo, @registroId = @Registro;

        FETCH NEXT FROM cambios_cursor INTO @Campo, @ValorNuevo;
    END

    CLOSE cambios_cursor;
    DEALLOCATE cambios_cursor;

    -- Marcar la petición como aceptada
    UPDATE PeticionRestauracion
    SET Estado = 'Aceptado'
    WHERE IdPeticion = @IdPeticion;
END

GO
