CREATE PROCEDURE [dbo].[sp_AceptarPeticionRestauracionExtension]
    @IdPeticion INT,
    @UsuarioAutorizador NVARCHAR(255)
AS
BEGIN
    SET NOCOUNT ON;

    DECLARE @IdCambio UNIQUEIDENTIFIER,
            @Tabla NVARCHAR(255),
            @Registro INT,
            @ColumnaClave NVARCHAR(255),
            @Esquema NVARCHAR(255) = 'dbo',
            @NombreTabla NVARCHAR(255),
            @TipoCambio CHAR(1);

    -- Variables para manejar IDENTITY_INSERT (declaradas solo una vez)
    DECLARE @sqlIdInsertOn NVARCHAR(MAX);
    DECLARE @sqlIdInsertOff NVARCHAR(MAX);

    -- Obtener datos de la petición
    SELECT @IdCambio = IdCambio, @Tabla = Tabla, @Registro = Registro
    FROM PeticionRestauracion
    WHERE IdPeticion = @IdPeticion AND Estado = 'Pendiente';

    IF @IdCambio IS NULL
    BEGIN
        THROW 51001, 'Petición no encontrada o ya procesada.', 1;
    END

    -- Obtener tipo de cambio (I, U, D, R)
    SELECT TOP 1 @TipoCambio = Tipo
    FROM AuditoriaTablas
    WHERE Id_Cambio = @IdCambio;

    IF @TipoCambio IS NULL
    BEGIN
        THROW 51002, 'No se encontró el tipo de cambio.', 1;
    END

    -- Separar esquema y tabla
    IF CHARINDEX('.', @Tabla) > 0
    BEGIN
        SET @Esquema = PARSENAME(@Tabla, 2);
        SET @NombreTabla = PARSENAME(@Tabla, 1);
    END
    ELSE
        SET @NombreTabla = @Tabla;

    -- Obtener clave primaria
    SELECT TOP 1 @ColumnaClave = KU.COLUMN_NAME
    FROM INFORMATION_SCHEMA.TABLE_CONSTRAINTS AS TC
    INNER JOIN INFORMATION_SCHEMA.KEY_COLUMN_USAGE AS KU
        ON TC.CONSTRAINT_NAME = KU.CONSTRAINT_NAME AND TC.TABLE_SCHEMA = KU.TABLE_SCHEMA
    WHERE TC.TABLE_NAME = @NombreTabla AND TC.TABLE_SCHEMA = @Esquema AND TC.CONSTRAINT_TYPE = 'PRIMARY KEY';

    IF @ColumnaClave IS NULL
    BEGIN
        THROW 51003, 'No se encontró la clave primaria.', 1;
    END

    -- Verificar si existe el registro
    DECLARE @sqlExiste NVARCHAR(MAX) = 
        'SELECT @out = COUNT(1) FROM ' + QUOTENAME(@Esquema) + '.' + QUOTENAME(@NombreTabla) + 
        ' WHERE ' + QUOTENAME(@ColumnaClave) + ' = @idRegistro';

    DECLARE @ExisteRegistro INT;
    EXEC sp_executesql @sqlExiste,
        N'@idRegistro INT, @out INT OUTPUT',
        @idRegistro = @Registro,
        @out = @ExisteRegistro OUTPUT;

    IF @ExisteRegistro IS NULL SET @ExisteRegistro = 0;

    -- Funcion auxiliar para ejecutar IDENTITY_INSERT solo si no es la tabla usuarios
    DECLARE @UsarIdentityInsert BIT = CASE WHEN LOWER(@NombreTabla) = 'usuarios' THEN 0 ELSE 1 END;

    IF @TipoCambio = 'D'
    BEGIN
        -- Delete físico
        IF @ExisteRegistro = 1
        BEGIN
            DECLARE @sqlDelete NVARCHAR(MAX) = 
                'DELETE FROM ' + QUOTENAME(@Esquema) + '.' + QUOTENAME(@NombreTabla) +
                ' WHERE ' + QUOTENAME(@ColumnaClave) + ' = @registroId';

            EXEC sp_executesql @sqlDelete,
                N'@registroId INT',
                @registroId = @Registro;
        END
    END
    ELSE IF @TipoCambio = 'I'
    BEGIN
        IF @ExisteRegistro = 1
        BEGIN
            THROW 51004, 'Registro ya existe, no se puede insertar.', 1;
        END

        -- Insertar registro con valores después de auditoria
        DECLARE @cols NVARCHAR(MAX);
        DECLARE @vals NVARCHAR(MAX);

        ;WITH CamposValores AS (
            SELECT Campo, ValorDespues
            FROM AuditoriaTablas
            WHERE Id_Cambio = @IdCambio
        )
        SELECT
            @cols = STRING_AGG(QUOTENAME(Campo), ', '),
            @vals = STRING_AGG('''' + REPLACE(ValorDespues, '''', '''''') + '''', ', ')
        FROM CamposValores;

        IF @UsarIdentityInsert = 1
        BEGIN
            SET @sqlIdInsertOn = 'SET IDENTITY_INSERT ' + QUOTENAME(@Esquema) + '.' + QUOTENAME(@NombreTabla) + ' ON';
            EXEC(@sqlIdInsertOn);
        END

        DECLARE @sqlInsert NVARCHAR(MAX) = 
            'INSERT INTO ' + QUOTENAME(@Esquema) + '.' + QUOTENAME(@NombreTabla) + 
            ' (' + @cols + ') VALUES (' + @vals + ')';

        EXEC(@sqlInsert);

        IF @UsarIdentityInsert = 1
        BEGIN
            SET @sqlIdInsertOff = 'SET IDENTITY_INSERT ' + QUOTENAME(@Esquema) + '.' + QUOTENAME(@NombreTabla) + ' OFF';
            EXEC(@sqlIdInsertOff);
        END
    END
    ELSE IF @TipoCambio = 'U'
    BEGIN
        IF @ExisteRegistro = 0
        BEGIN
            -- Restaurar registro

            DECLARE @IdCambioRestore UNIQUEIDENTIFIER;

            SELECT TOP 1 @IdCambioRestore = Id_Cambio
            FROM AuditoriaTablas
            WHERE Tabla = @Tabla AND Registro = @Registro AND Tipo IN ('I','U')
            ORDER BY Fecha DESC;

            IF @IdCambioRestore IS NULL
            BEGIN
                THROW 51005, 'No se encontró cambio para restaurar el registro antes del update.', 1;
            END

            DECLARE @colsRestore NVARCHAR(MAX);
            DECLARE @valsRestore NVARCHAR(MAX);

            ;WITH CamposRestore AS (
                SELECT Campo, ValorDespues
                FROM AuditoriaTablas
                WHERE Id_Cambio = @IdCambioRestore
            )
            SELECT
                @colsRestore = STRING_AGG(QUOTENAME(Campo), ', '),
                @valsRestore = STRING_AGG('''' + REPLACE(ValorDespues, '''', '''''') + '''', ', ')
            FROM CamposRestore;

            IF @UsarIdentityInsert = 1
            BEGIN
                SET @sqlIdInsertOn = 'SET IDENTITY_INSERT ' + QUOTENAME(@Esquema) + '.' + QUOTENAME(@NombreTabla) + ' ON';
                EXEC(@sqlIdInsertOn);
            END

            DECLARE @sqlInsertRestore NVARCHAR(MAX) = 
                'INSERT INTO ' + QUOTENAME(@Esquema) + '.' + QUOTENAME(@NombreTabla) + 
                ' (' + @colsRestore + ') VALUES (' + @valsRestore + ')';

            EXEC(@sqlInsertRestore);

            IF @UsarIdentityInsert = 1
            BEGIN
                SET @sqlIdInsertOff = 'SET IDENTITY_INSERT ' + QUOTENAME(@Esquema) + '.' + QUOTENAME(@NombreTabla) + ' OFF';
                EXEC(@sqlIdInsertOff);
            END
        END

        -- Actualizar registro

        DECLARE @setUpdates NVARCHAR(MAX);

        SELECT @setUpdates = STRING_AGG(QUOTENAME(Campo) + ' = ''' + REPLACE(ValorDespues, '''', '''''') + '''', ', ')
        FROM AuditoriaTablas
        WHERE Id_Cambio = @IdCambio;

        IF @setUpdates IS NULL OR LTRIM(RTRIM(@setUpdates)) = ''
        BEGIN
            THROW 51006, 'No hay campos para actualizar.', 1;
        END

        DECLARE @sqlUpdate NVARCHAR(MAX) = 
            'UPDATE ' + QUOTENAME(@Esquema) + '.' + QUOTENAME(@NombreTabla) + 
            ' SET ' + @setUpdates + ' WHERE ' + QUOTENAME(@ColumnaClave) + ' = @registroId';

        EXEC sp_executesql @sqlUpdate,
            N'@registroId INT',
            @registroId = @Registro;
    END

    -- Guardar en auditoría que se hizo restore (tipo R)
    INSERT INTO AuditoriaTablas (Tabla, Campo, ValorAntes, ValorDespues, Fecha, Tipo, Registro, Id_Cambio, Usuario)
    SELECT Tabla, Campo, ValorDespues, ValorDespues, GETDATE(), 'R', Registro, NEWID(), @UsuarioAutorizador
    FROM AuditoriaTablas
    WHERE Id_Cambio = @IdCambio;

    -- Actualizar estado de petición
    UPDATE PeticionRestauracion
    SET Estado = 'Aceptado'
    WHERE IdPeticion = @IdPeticion;

END

