CREATE PROCEDURE [dbo].[sp_AsignarPermiso]
    @usuario_id INT,
    @permiso_id INT
AS
BEGIN
    SET NOCOUNT ON;

    DECLARE @es_rol BIT;

    SELECT @es_rol = es_rol 
    FROM [dbo].[permiso] 
    WHERE [id] = @permiso_id;

    IF EXISTS (SELECT 1 FROM [dbo].[permiso] WHERE [id] = @permiso_id AND [habilitado] = 1)
    BEGIN
        IF @es_rol = 1
        BEGIN
