CREATE PROCEDURE [dbo].[sp_AsignarRolARol]
    @idRolPadre INT,  
    @idRolHijo INT    
AS
BEGIN
    BEGIN TRY
        -- Validar que el rol padre existe y está habilitado
        IF NOT EXISTS (SELECT 1 FROM permiso WHERE id = @idRolPadre AND es_rol = 1 AND habilitado = 1)
        BEGIN
            RAISERROR('El rol padre no es válido o no está habilitado.', 16, 1);
            RETURN;
        END;

        -- Validar que el rol hijo existe y está habilitado
        IF NOT EXISTS (SELECT 1 FROM permiso WHERE id = @idRolHijo AND es_rol = 1 AND habilitado = 1)
        BEGIN
            RAISERROR('El rol hijo no es válido o no está habilitado.', 16, 1);
            RETURN;
        END;

        -- Validar que no se asignen a sí mismos
        IF @idRolPadre = @idRolHijo
        BEGIN
            RAISERROR('Un rol no puede asignarse a sí mismo.', 16, 1);
            RETURN;
        END;

        -- Verificación ascendente: Verificar que el rol hijo no esté en la jerarquía ascendente del rol padre
        IF EXISTS (SELECT 1
                   FROM permiso_permiso pp
                   WHERE pp.id_permiso_hijo = @idRolPadre
                   AND pp.id_permiso_padre = @idRolHijo)
        BEGIN
            RAISERROR('La asignación del rol hijo al rol padre provocaría una referencia circular en la jerarquía ascendente.', 16, 1);
            RETURN;
        END;

        -- Verificación descendente: Verificar que el rol hijo no esté en la jerarquía descendente del rol padre
        IF EXISTS (SELECT 1
                   FROM permiso_permiso pp
                   WHERE pp.id_permiso_padre = @idRolPadre
                   AND pp.id_permiso_hijo = @idRolHijo)
        BEGIN
            RAISERROR('La asignación del rol hijo al rol padre provocaría una referencia circular en la jerarquía descendente.', 16, 1);
            RETURN;
        END;

        -- Nueva validación para evitar la creación de relaciones indirectas
        -- Verificamos si el rol hijo ya está en alguna parte de la jerarquía del rol padre, ya sea de forma directa o indirecta.
        DECLARE @RolHijoIndirecto INT;

        WITH Jerarquia AS (
            SELECT id_permiso_padre, id_permiso_hijo
            FROM permiso_permiso
            WHERE id_permiso_padre = @idRolPadre
            UNION ALL
            SELECT pp.id_permiso_padre, pp.id_permiso_hijo
            FROM permiso_permiso pp
            INNER JOIN Jerarquia j ON j.id_permiso_hijo = pp.id_permiso_padre
        )
        SELECT @RolHijoIndirecto = 1
        FROM Jerarquia
        WHERE id_permiso_hijo = @idRolHijo;

        IF @RolHijoIndirecto = 1
        BEGIN
            RAISERROR('El rol hijo ya está presente en la jerarquía del rol padre, lo que provocaría una referencia circular indirecta.', 16, 1);
            RETURN;
        END;

        -- Insertar la relación (solo si no se encontró un ciclo)
        INSERT INTO permiso_permiso (id_permiso_padre, id_permiso_hijo)
        VALUES (@idRolPadre, @idRolHijo);

    END TRY
    BEGIN CATCH
        -- Capturar el error real y lanzar un mensaje personalizado con el error original
        DECLARE @ErrorMessage NVARCHAR(4000);
        DECLARE @ErrorSeverity INT;
        DECLARE @ErrorState INT;

        -- Obtener el error capturado
        SELECT 
            @ErrorMessage = ERROR_MESSAGE(),
            @ErrorSeverity = ERROR_SEVERITY(),
            @ErrorState = ERROR_STATE();

        -- Lanzar un error personalizado con el mensaje real del error
        RAISERROR('Error: %s', @ErrorSeverity, @ErrorState, @ErrorMessage);
    END CATCH
END;

