CREATE PROCEDURE [dbo].[sp_CrearPeticionRestauracion]
    @IdCambio UNIQUEIDENTIFIER,
    @Tabla NVARCHAR(255),
    @UsuarioSolicitante NVARCHAR(255),
    @Registro INT,
    @Comentario NVARCHAR(MAX)
AS
BEGIN
    SET NOCOUNT ON;

    -- Contar total de campos del cambio propuesto
    DECLARE @TotalCampos INT;
    SELECT @TotalCampos = COUNT(*)
    FROM AuditoriaTablas
    WHERE id_cambio = @IdCambio;

    -- Contar cuántos campos ya tienen como último cambio el mismo id_cambio (ya aplicado)
    DECLARE @YaAplicados INT;
    SELECT @YaAplicados = COUNT(*)
    FROM AuditoriaTablas AS propuesto
    CROSS APPLY (
        SELECT TOP 1 id_cambio
        FROM AuditoriaTablas
        WHERE Tabla = propuesto.Tabla
          AND Registro = propuesto.Registro
          AND Campo = propuesto.Campo
        ORDER BY Fecha DESC
    ) AS actual
    WHERE propuesto.id_cambio = @IdCambio
      AND actual.id_cambio = @IdCambio;

    -- Si todos los campos ya tienen como último cambio este id_cambio, se rechaza
    IF @YaAplicados = @TotalCampos
    BEGIN
        THROW 51000, 'El estado actual del registro ya proviene del mismo cambio propuesto. No se puede crear la petición.', 1;
    END
    ELSE
    BEGIN
        INSERT INTO PeticionRestauracion (
            IdCambio,
            Tabla,
            UsuarioSolicitante,
            FechaSolicitud,
            Estado,
            Registro,
            Comentario
        )
        VALUES (
            @IdCambio,
            @Tabla,
            @UsuarioSolicitante,
            GETDATE(),
            'Pendiente',
            @Registro,
            @Comentario
        );
    END
END

