CREATE PROCEDURE [dbo].[sp_DesasignarPermisoDeRol]
    @idRol INT,         -- ID del rol del que se quiere desasignar el permiso
    @idPermiso INT      -- ID del permiso que se quiere desasignar
AS
BEGIN
    BEGIN TRY
        -- Verificar que el rol existe, es válido y está habilitado
        IF NOT EXISTS (SELECT 1 FROM permiso WHERE id = @idRol AND es_rol = 1 AND habilitado = 1)
        BEGIN
            SELECT 'El rol no es válido o no está habilitado.' AS Mensaje, 0 AS Resultado;
            RETURN;
        END;

        -- Verificar que el permiso existe y es válido
        IF NOT EXISTS (SELECT 1 FROM permiso WHERE id = @idPermiso AND es_rol = 0 AND habilitado = 1)
        BEGIN
            SELECT 'El permiso no es válido o no está habilitado.' AS Mensaje, 0 AS Resultado;
            RETURN;
        END;

        -- Obtener el ID y el nombre del rol padre asociado al permiso
        DECLARE @idPermisoPadre INT;
        DECLARE @nombreRolPadre NVARCHAR(255);

        SELECT 
            @idPermisoPadre = id_permiso_padre,
            @nombreRolPadre = p.nombre
        FROM permiso_permiso pp
        INNER JOIN permiso p ON pp.id_permiso_padre = p.id
        WHERE pp.id_permiso_hijo = @idPermiso;

        -- Si el permiso no tiene un rol asociado
        IF @idPermisoPadre IS NULL
        BEGIN
            SELECT 'El permiso no está asignado a ningún rol.' AS Mensaje, 0 AS Resultado;
            RETURN;
        END;

        -- Verificar si el rol padre del permiso es diferente del rol especificado
        IF @idPermisoPadre <> @idRol
        BEGIN
            SELECT 
                'El permiso pertenece al rol "' + @nombreRolPadre + '". Desasigne este permiso del rol correspondiente.' AS Mensaje, 
                0 AS Resultado;
            RETURN;
        END;

        -- Verificar si la relación entre el rol y el permiso existe
        IF NOT EXISTS (SELECT 1 FROM permiso_permiso WHERE id_permiso_padre = @idRol AND id_permiso_hijo = @idPermiso)
        BEGIN
            SELECT 'La relación entre el rol y el permiso no existe.' AS Mensaje, 0 AS Resultado;
            RETURN;
        END;

        -- Eliminar la relación
        DELETE FROM permiso_permiso
        WHERE id_permiso_padre = @idRol AND id_permiso_hijo = @idPermiso;

        -- Devolver mensaje de éxito
        SELECT 'El permiso ha sido desasignado correctamente del rol.' AS Mensaje, 1 AS Resultado;
    END TRY
    BEGIN CATCH
        -- Manejo de errores: Devolver mensaje de error específico
        SELECT 
            ERROR_MESSAGE() AS Mensaje, 
            0 AS Resultado;
    END CATCH
END;

