        END;

        -- Verificar que el rol hijo existe, es válido y está habilitado
        IF NOT EXISTS (SELECT 1 FROM permiso WHERE id = @idRolHijo AND es_rol = 1 AND habilitado = 1)
        BEGIN
            SELECT 'El rol hijo no es válido o no está habilitado.' AS Mensaje, 0 AS Resultado;
            RETURN;
        END;

        -- Verificar si la relación entre el rol padre y hijo existe
        IF NOT EXISTS (SELECT 1 FROM permiso_permiso WHERE id_permiso_padre = @idRolPadre AND id_permiso_hijo = @idRolHijo)
        BEGIN
            SELECT 'La relación entre el rol padre y el rol hijo no existe.' AS Mensaje, 0 AS Resultado;
            RETURN;
        END;

        -- Eliminar la relación entre el rol padre y el rol hijo
        DELETE FROM permiso_permiso
        WHERE id_permiso_padre = @idRolPadre AND id_permiso_hijo = @idRolHijo;

        -- Verificar si el rol hijo todavía está asignado a otros roles padres
        IF EXISTS (SELECT 1 FROM permiso_permiso WHERE id_permiso_hijo = @idRolHijo)
        BEGIN
            -- Si el rol hijo está asignado a otros roles padres, retornar mensaje de éxito
            SELECT 'El rol hijo ha sido desasignado correctamente del rol padre, pero sigue asignado a otros roles padres.' AS Mensaje, 1 AS Resultado;
        END
        ELSE
        BEGIN
            -- Si el rol hijo ya no está asignado a ningún rol padre, retornar mensaje de éxito
            SELECT 'El rol hijo ha sido desasignado correctamente y ya no está asignado a ningún rol padre.' AS Mensaje, 1 AS Resultado;
        END;
    END TRY
    BEGIN CATCH
        -- Manejo de errores: Devolver mensaje de error específico
        SELECT 
            ERROR_MESSAGE() AS Mensaje, 
            0 AS Resultado;
    END CATCH
END;

