CREATE PROCEDURE [dbo].[sp_DeshacerUltimoCambio]
    @usuario NVARCHAR(255),
    @pc NVARCHAR(255)
AS
BEGIN
    SET NOCOUNT ON;

    DECLARE @id_cambio_target UNIQUEIDENTIFIER;
    DECLARE @fecha DATETIME = GETDATE();
    DECLARE @tabla NVARCHAR(255), @registro_id INT, @campo NVARCHAR(255),
            @valorAntes NVARCHAR(MAX), @sql NVARCHAR(MAX),
            @nuevo_id_cambio UNIQUEIDENTIFIER;

    -- Buscar el último id_cambio que no sea de tipo 'Z'
    SELECT TOP 1 @id_cambio_target = id_cambio
    FROM AuditoriaTablas
    WHERE Tipo <> 'Z'
    ORDER BY Fecha DESC;

    -- Generar nuevo id_cambio para esta reversión
    SET @nuevo_id_cambio = NEWID();

    -- Cursor para recorrer los cambios a revertir
    DECLARE cur CURSOR FOR
        SELECT Tabla, Registro, Campo, ValorAntes
        FROM AuditoriaTablas
        WHERE id_cambio = @id_cambio_target;

    OPEN cur;
    FETCH NEXT FROM cur INTO @tabla, @registro_id, @campo, @valorAntes;

    WHILE @@FETCH_STATUS = 0
    BEGIN
        -- Limpiar nombre de tabla
        SET @tabla = REPLACE(REPLACE(@tabla, '[', ''), ']', '');

        -- Preparar la reversión del campo
        SET @sql = 'UPDATE ' + @tabla + ' SET ' + @campo + ' = ' +
                   CASE 
                       WHEN @valorAntes IS NULL THEN 'NULL'
                       ELSE '''' + REPLACE(@valorAntes, '''', '''''') + ''''
                   END +
                   ' WHERE ' + @tabla + '_id = ' + CAST(@registro_id AS NVARCHAR);

        EXEC sp_executesql @sql;

        -- Registrar el cambio tipo 'Z'
        SET @sql = '
            INSERT INTO AuditoriaTablas (
                Tipo, Tabla, Registro, Campo, ValorAntes, ValorDespues,
                Fecha, Usuario, PC, id_cambio
            )
            SELECT
                ''Z'',
                ''[' + @tabla + ']'',
                ' + CAST(@registro_id AS NVARCHAR) + ',
                ''' + @campo + ''',
                CAST(' + @campo + ' AS NVARCHAR(MAX)),
                ' + CASE 
                        WHEN @valorAntes IS NULL THEN 'NULL'
                        ELSE '''''' + REPLACE(@valorAntes, '''', '''''') + ''''''
                  END + ',
                ''' + CONVERT(NVARCHAR, @fecha, 121) + ''',
                ''' + @usuario + ''',
                ''' + @pc + ''',
                ''' + CAST(@nuevo_id_cambio AS NVARCHAR(50)) + '''
            FROM ' + @tabla + '
            WHERE ' + @tabla + '_id = ' + CAST(@registro_id AS NVARCHAR) + ';
        ';

        EXEC sp_executesql @sql;

        FETCH NEXT FROM cur INTO @tabla, @registro_id, @campo, @valorAntes;
    END

    CLOSE cur;
    DEALLOCATE cur;
END;

