CREATE PROCEDURE [dbo].[sp_EliminarRol]
    @IdRol INT
AS
BEGIN
    SET NOCOUNT ON;

    BEGIN TRY
        -- 1. Verificar si el rol está asignado a algún usuario
        IF EXISTS (SELECT 1 FROM usuarios WHERE id_rol = @IdRol)
        BEGIN
            -- Obtener los nombres de los usuarios afectados
            SELECT 'El rol no puede ser eliminado porque está asignado a uno o mas usuarios.' AS Mensaje, 0 AS Resultado;
            
            SELECT Username AS Usuario
            FROM usuarios 
            WHERE id_rol = @IdRol;

            RETURN;
        END

        -- 2. Eliminar relaciones donde el rol es hijo
        DELETE FROM permiso_permiso 
        WHERE id_permiso_hijo = @IdRol;

        -- 3. Eliminar relaciones donde el rol es padre
        DELETE FROM permiso_permiso 
        WHERE id_permiso_padre = @IdRol;

        -- 4. Eliminar el rol de la tabla permiso
        DELETE FROM permiso 
        WHERE id = @IdRol AND es_rol = 1;

        -- 5. Retornar mensaje de éxito
        SELECT 'Rol eliminado correctamente.' AS Mensaje, 1 AS Resultado;
    END TRY
    BEGIN CATCH
        -- Manejo de errores
        SELECT 
            'Error al intentar eliminar el rol.' AS Mensaje,
            ERROR_MESSAGE() AS ErrorDetalle,
            0 AS Resultado;
    END CATCH
END;

