CREATE PROCEDURE [dbo].[sp_ObtenerPermisosDelRol]
    @nombre_rol NVARCHAR(255)
AS
BEGIN
    SET NOCOUNT ON;

    BEGIN TRY
        -- Declarar variable para almacenar el ID del rol
        DECLARE @id_rol INT;

        -- Obtener el ID del rol con el nombre proporcionado
        SELECT @id_rol = id
        FROM [dbo].[permiso]
        WHERE nombre = @nombre_rol AND es_rol = 1 AND habilitado = 1;

        -- Verificar si el rol existe
        IF @id_rol IS NULL
        BEGIN
            RAISERROR('El rol especificado no existe o no es un rol válido.', 16, 1);
            RETURN;
        END;

        -- CTE para obtener los permisos recursivamente
        WITH PermisosRecursivos AS (
            -- Nivel base: incluir el rol seleccionado como raíz
            SELECT 
                p.id AS id_permiso,
                p.nombre AS nombre_permiso,
                p.habilitado,
                p.es_rol,
                p.permiso,
                NULL AS id_padre, -- No tiene padre ya que es la raíz
                p.id AS id_hijo,
                0 AS nivel -- Nivel raíz
            FROM permiso p
            WHERE p.id = @id_rol

            UNION ALL

            -- Nivel recursivo: selecciona los hijos de los permisos anteriores
            SELECT 
                ph.id AS id_permiso,
                ph.nombre AS nombre_permiso,
                ph.habilitado,
                ph.es_rol,
                ph.permiso,
                pr.id_permiso AS id_padre,
                ph.id AS id_hijo,
                pr.nivel + 1 AS nivel
            FROM PermisosRecursivos pr
            JOIN permiso_permiso pp ON pr.id_permiso = pp.id_permiso_padre
            JOIN permiso ph ON pp.id_permiso_hijo = ph.id
        )

        -- Selecciona los permisos únicos y su jerarquía
        SELECT DISTINCT
            id_permiso,
            nombre_permiso,
            habilitado,
            es_rol,
            permiso,
            id_padre AS id_permiso_padre,
            id_hijo,
            nivel
        FROM PermisosRecursivos
        ORDER BY nivel, id_padre, id_permiso; -- Orden jerárquico
    END TRY
    BEGIN CATCH
        -- Manejo de errores
        SELECT 
            ERROR_NUMBER() AS ErrorNumero,
            ERROR_MESSAGE() AS ErrorMensaje;
    END CATCH
END;

GO
