CREATE PROCEDURE [dbo].[sp_ObtenerPermisosUsuario]
    @id_usuario INT
AS
BEGIN
    SET NOCOUNT ON;

    -- Obtener el rol principal del usuario
    DECLARE @rolId INT;
    SELECT @rolId = id_rol
    FROM [dbo].[usuarios]
    WHERE id_usuario = @id_usuario;

    -- Verificar que el usuario tiene un rol asignado
    IF @rolId IS NULL
    BEGIN
        RAISERROR ('El usuario no tiene un rol asignado.', 16, 1);
        RETURN;
    END;

    -- Utilizar una CTE para obtener permisos de manera recursiva
    WITH PermisosRecursivos AS (
        -- Nivel base: incluir el rol principal del usuario como raíz
        SELECT 
            p.id AS id_permiso,
            p.nombre AS nombre_permiso,
            p.es_rol,
            p.habilitado,
            NULL AS id_padre, -- No tiene padre ya que es la raíz
            p.id AS id_hijo,
            0 AS nivel -- Nivel raíz
        FROM permiso p
        WHERE p.id = @rolId
          AND p.habilitado = 1 

        UNION ALL

        -- Nivel recursivo: permisos hijos de roles y permisos anteriores
        SELECT 
            ph.id AS id_permiso,
            ph.nombre AS nombre_permiso,
            ph.es_rol,
            ph.habilitado,
            pr.id_permiso AS id_padre,
            ph.id AS id_hijo,
            pr.nivel + 1 AS nivel
        FROM PermisosRecursivos pr
        JOIN [dbo].[permiso_permiso] pp ON pr.id_permiso = pp.id_permiso_padre
        JOIN [dbo].[permiso] ph ON pp.id_permiso_hijo = ph.id
        WHERE ph.habilitado = 1
    )

    -- Seleccionar todos los permisos obtenidos
    SELECT DISTINCT 
        id_permiso,
        nombre_permiso,
        es_rol,
        habilitado,
        id_padre AS id_permiso_padre,
        id_hijo,
        nivel
    FROM PermisosRecursivos
    ORDER BY nivel, id_permiso_padre, id_permiso;
END;

