CREATE PROCEDURE [dbo].[sp_RestablecerCambios]
    @idAuditoria INT
AS
BEGIN
    -- Variables para almacenar el nombre de la tabla y los valores
    DECLARE @Tabla NVARCHAR(128);
    DECLARE @Registro INT;
    DECLARE @Campo NVARCHAR(128);
    DECLARE @ValorAntes NVARCHAR(MAX);
    DECLARE @SQL NVARCHAR(MAX);

    -- Cursor para recorrer los registros de la tabla de auditoría según el idAuditoria
    DECLARE auditoria_cursor CURSOR FOR
    SELECT Tabla, Registro, Campo, ValorAntes
    FROM dbo.AuditoriaTablas
    WHERE IdAuditoria = @idAuditoria;

    OPEN auditoria_cursor;

    FETCH NEXT FROM auditoria_cursor INTO @Tabla, @Registro, @Campo, @ValorAntes;

    -- Bucle para procesar todos los cambios
    WHILE @@FETCH_STATUS = 0
    BEGIN
        -- Construir la consulta SQL dinámica para actualizar la tabla original
        SET @SQL = N'UPDATE ' + QUOTENAME(@Tabla) + 
                   N' SET ' + QUOTENAME(@Campo) + N' = @ValorAntes ' +
                   N' WHERE Id = @Registro';

        -- Ejecutar la consulta dinámica
        EXEC sp_executesql @SQL, N'@ValorAntes NVARCHAR(MAX), @Registro INT', @ValorAntes, @Registro;

        -- Obtener el siguiente registro del cursor
        FETCH NEXT FROM auditoria_cursor INTO @Tabla, @Registro, @Campo, @ValorAntes;
    END;

    -- Cerrar y liberar el cursor
    CLOSE auditoria_cursor;
    DEALLOCATE auditoria_cursor;
END;

