    PRINT '    DECLARE @PC VARCHAR(128) = HOST_NAME();';
    PRINT '    DECLARE @id_cambio UNIQUEIDENTIFIER = NEWID();';
    PRINT '';

    DECLARE @ClavePK VARCHAR(50) = (
        SELECT TOP 1 Columna FROM #InfoTabla WHERE PrimaryKey = 1 ORDER BY column_id
    );

    DECLARE columnas_cursor CURSOR FOR
        SELECT Columna FROM #InfoTabla;

    OPEN columnas_cursor;
    FETCH NEXT FROM columnas_cursor INTO @Campo;

    WHILE @@FETCH_STATUS = 0
    BEGIN
        PRINT '    INSERT INTO dbo.AuditoriaTablas (Tabla, Campo, ValorAntes, ValorDespues, Usuario, PC, Fecha, Tipo, Registro, id_cambio)';

        IF @ClavePK IS NOT NULL
        BEGIN
            PRINT '    SELECT ''' + @TablaCompleta + ''', ''' + @Campo + ''', NULL, CAST(i.' + QUOTENAME(@Campo) + ' AS NVARCHAR(MAX)), @Usuario, @PC, GETDATE(), ''I'', CAST(i.' + QUOTENAME(@ClavePK) + ' AS NVARCHAR(MAX)), @id_cambio';
            PRINT '    FROM INSERTED i;';
        END
        ELSE
        BEGIN
            PRINT '    SELECT ''' + @TablaCompleta + ''', ''' + @Campo + ''', NULL, CAST(i.' + QUOTENAME(@Campo) + ' AS NVARCHAR(MAX)), @Usuario, @PC, GETDATE(), ''I'', NULL, @id_cambio';
            PRINT '    FROM INSERTED i;';
        END

        PRINT '';
        FETCH NEXT FROM columnas_cursor INTO @Campo;
    END

    CLOSE columnas_cursor;
    DEALLOCATE columnas_cursor;

    PRINT 'END';
    PRINT 'GO';

