    DECLARE @Campo VARCHAR(50);
    DECLARE @TablaCompleta VARCHAR(255) = QUOTENAME(@Esquema) + '.' + QUOTENAME(@Tabla);
    DECLARE @NombreTrigger VARCHAR(255) = QUOTENAME(@Esquema) + '.[' + 'tr_' + @Tabla + '_AuditoriaUpdate]';

    SELECT
        c.name AS Columna,
        t.name AS Tipo,
        c.max_length AS Longitud,
        c.precision,
        c.scale,
        c.is_nullable,
        ISNULL(i.is_primary_key, 0) AS PrimaryKey,
        c.column_id
    INTO #InfoTabla
    FROM sys.columns c
    INNER JOIN sys.types t ON c.user_type_id = t.user_type_id
    LEFT OUTER JOIN sys.index_columns ic ON ic.object_id = c.object_id AND ic.column_id = c.column_id
    LEFT OUTER JOIN sys.indexes i ON ic.object_id = i.object_id AND ic.index_id = i.index_id
    WHERE c.object_id = OBJECT_ID(@TablaCompleta)
    ORDER BY c.column_id;

    DECLARE @NombreClavePrimaria VARCHAR(50) = (
        SELECT TOP 1 Columna FROM #InfoTabla WHERE PrimaryKey = 1 ORDER BY column_id
    );

    PRINT 'IF OBJECT_ID(''' + @NombreTrigger + ''', ''TR'') IS NOT NULL DROP TRIGGER ' + @NombreTrigger + ';';
    PRINT 'GO';
    PRINT 'CREATE TRIGGER ' + @NombreTrigger;
    PRINT 'ON ' + @TablaCompleta + ' FOR UPDATE AS';
    PRINT 'BEGIN';
    PRINT '    DECLARE @Usuario VARCHAR(128) = SYSTEM_USER;';
    PRINT '    DECLARE @PC VARCHAR(128) = HOST_NAME();';
    PRINT '    DECLARE @IdCambio UNIQUEIDENTIFIER = NEWID();';
    PRINT '';

    DECLARE columnas_cursor CURSOR FOR
        SELECT Columna FROM #InfoTabla;

    OPEN columnas_cursor;
    FETCH NEXT FROM columnas_cursor INTO @Campo;

    WHILE @@FETCH_STATUS = 0
    BEGIN
        PRINT '    INSERT INTO dbo.AuditoriaTablas (Tabla, Campo, ValorAntes, ValorDespues, Usuario, PC, Fecha, Tipo, Registro, id_cambio)';
        PRINT '    SELECT ''' + @TablaCompleta + ''', ''' + @Campo + ''',';
        PRINT '           CAST(DELETED.' + QUOTENAME(@Campo) + ' AS NVARCHAR(MAX)),';
        PRINT '           CAST(INSERTED.' + QUOTENAME(@Campo) + ' AS NVARCHAR(MAX)),';
        PRINT '           @Usuario, @PC, GETDATE(), ''U'',';
        IF @NombreClavePrimaria IS NOT NULL
            PRINT '           CAST(INSERTED.' + QUOTENAME(@NombreClavePrimaria) + ' AS NVARCHAR(MAX)),';
        ELSE
            PRINT '           NULL,';
        PRINT '           @IdCambio';
        PRINT '    FROM INSERTED';
        IF @NombreClavePrimaria IS NOT NULL
            PRINT '    INNER JOIN DELETED ON INSERTED.' + QUOTENAME(@NombreClavePrimaria) + ' = DELETED.' + QUOTENAME(@NombreClavePrimaria);
        PRINT '    WHERE ISNULL(CAST(INSERTED.' + QUOTENAME(@Campo) + ' AS NVARCHAR(MAX)), '''') <> ISNULL(CAST(DELETED.' + QUOTENAME(@Campo) + ' AS NVARCHAR(MAX)), '''');';
        PRINT '';

        FETCH NEXT FROM columnas_cursor INTO @Campo;
    END

    CLOSE columnas_cursor;
    DEALLOCATE columnas_cursor;

    PRINT 'END';
    PRINT 'GO';

    DROP TABLE #InfoTabla;
END;

