CREATE PROCEDURE [dbo].[spGenerarAuditoriaTodasLasTablas]
AS
BEGIN
    SET NOCOUNT ON;

    DECLARE @Esquema NVARCHAR(128);
    DECLARE @Tabla NVARCHAR(128);
    DECLARE @Comando NVARCHAR(MAX);

    -- Cursor para recorrer todas las tablas de usuario (excepto las del sistema)
    DECLARE tablas_cursor CURSOR FOR
    SELECT
        s.name AS Esquema,
        t.name AS Tabla
    FROM sys.tables t
    INNER JOIN sys.schemas s ON t.schema_id = s.schema_id
    WHERE t.is_ms_shipped = 0
    ORDER BY s.name, t.name;

    OPEN tablas_cursor;
    FETCH NEXT FROM tablas_cursor INTO @Esquema, @Tabla;

    WHILE @@FETCH_STATUS = 0
    BEGIN
        PRINT '----------------------------------------------------------';
        PRINT '-- Generando triggers para ' + QUOTENAME(@Esquema) + '.' + QUOTENAME(@Tabla);
        PRINT '----------------------------------------------------------';

        EXEC spGenerarAuditoriaTablaInsert @Esquema, @Tabla;
        EXEC spGenerarAuditoriaTablaUpdate @Esquema, @Tabla;
        EXEC spGenerarAuditoriaTablaDelete @Esquema, @Tabla;

        FETCH NEXT FROM tablas_cursor INTO @Esquema, @Tabla;
    END

    CLOSE tablas_cursor;
    DEALLOCATE tablas_cursor;
END;


